	(1, 'Investment Status Update', 'investment-status-update', 'Status of Investment has been updated!', 'Hi {user},\r\n\r\n                                <br><br><b>\r\n                                Transaction of Investment #{uuid} has been updated to {status} by system administrator!</b>\r\n\r\n                                <br><br>If you have any questions, please feel free to reply to this email.\r\n\r\n                                <br><br>Regards,\r\n                                <br><b>{soft_name}</b>', 'en', 'email', 'Active', 'Investment', '2023-07-06 03:26:43', NULL),
